import React, { useState, useEffect } from 'react';
import CommonSection from '../shared/CommonSection';
import { Container, Row, Col } from 'reactstrap';
import TourCard from '../shared/TourCard';
import NewsLetter from '../shared/NewsLetter';
import axios from 'axios'; // Import Axios for making HTTP requests

const SearchResultList = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    // Define a function to fetch tour search results
    const fetchTourData = async () => {
      try {
        // Make an HTTP GET request to fetch tour search results
        const response = await axios.get('/api/tours'); // Example URL, replace with your actual API endpoint
        setData(response.data); // Update 'data' state with fetched results
      } catch (error) {
        console.error('Error fetching data:', error);
        // Handle errors here, such as setting an error state or showing an error message
      }
    };

    // Call the fetchTourData function when the component mounts
    fetchTourData();
  }, []); // Run this effect only once when component mounts

  return (
    <>
      <CommonSection title={'Tour search result'} />
      <section>
        <Container>
          <Row>
            {data.length === 0 ? (
              <h4 className='text-center'>No tour found</h4>
            ) : (
              data.map((tour) => (
                <Col lg='3' className='mb-4' key={tour._id}>
                  <TourCard tour={tour} />
                </Col>
              ))
            )}
          </Row>
        </Container>
      </section>
      <NewsLetter />
    </>
  );
};

export default SearchResultList;
